  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const dispatch = useDispatch();
  const [selectAction, setSelectAction] = useState("");
  const [shipping, setShipping] = useState(16);

  const handleDecrement = (productId: any) => {
    dispatch(decrementQuantity(productId));
  };

  const handleDeleteCart = (productId: any) => {
    dispatch(removeFromCart(productId));
  };

  const handleDeleteWishlist = (productId: any) => {
    dispatch(removeFromWishlist(productId));
  };

  const handleIncrement = (productId: any) => {
    dispatch(incrementQuantity(productId));
  };

  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleMobileMenuToggle = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const wishlist = useSelector(productSelectors.selectWishlist);
  const cart = useSelector(productSelectors.selectCart);

  // Get the length of wishlist and cart arrays
  const wishlistLength = wishlist.length;
  const cartLength = cart.length;

  const calculateSubtotal = () => {
    return cart.reduce((total, item) => {
      return total + item.count * item.price;
    }, 0);
  };

  const calculateVAT = (subtotal: number) => {
    // Assuming VAT is 20%
    const vatRate = 0.2;
    return subtotal * vatRate;
  };

  const calculateTotal = (subtotal: number, shipping: number, vat: number) => {
    return subtotal + shipping + vat;
  };

  const subtotal = calculateSubtotal();
  const vat = calculateVAT(subtotal);
  const total = calculateTotal(subtotal, shipping, vat);

  const checkProduct = (params: string) => {
    if (params === "") {
      return;
    }

    setSelectAction(params);
    handleOpen();
  };

